import { ProductService } from '../../src/services/product.service';
import { Product, ProductAttributes } from '../../src/models/product.model';
import { User, UserAttributes } from '../../src/models/user.model';
import { expect } from 'chai';

describe('ProductService Tests', () => {

    const testProductService: ProductService = new ProductService();

    const user1: UserAttributes = {
        userId: 1,
        userName: 'Luca',
        password: 'notSecure10',
        email: 'luca@gmail.com',
        firstName: 'Luca',
        lastName: 'Schaller',
        gender: null,
        telephoneNumber: null,
        street: null,
        pinCode: null,
        city: 'Schmitten',
        country: null,
        admin: false,
        wallet: 500
    };

    const product1: ProductAttributes = {
        productId: 1,
        isApproved: false,
        title: 'Phone',
        isProduct: true,
        price: 300,
        description: 'IPhone X',
        location: 'Bern',
        isSelling: true,
        isAvailable: true,
        isDeliverable: true,
        rejectionReason: null,
        userId: 1,
        buyerId: null,
        dateBought: null,
        deletedAfterSold: false
    };

    before('create admin', function() {
        User.create(user1);
    });

    describe('Test product listing', () => {
        it('can list a product successfully', function() {
            testProductService.create(product1).then(product => {
                expect(product.title).to.be.eq('Phone');
                expect(product.price).to.be.eq(300);
                expect(product.rejectionReason).to.be.null;
                Product.findOne({
                    where: {
                        title: 'Phone'
                    }
                }).then(foundProduct => {
                    expect(foundProduct).not.to.be.null;
                });
            });
        });
        it('can not list product with missing necassary attributes', function() {
            const product10: ProductAttributes = {
                productId: 1,
                isApproved: false,
                title: null,
                isProduct: true,
                price: 300,
                description: 'IPhone X',
                location: 'Bern',
                isSelling: true,
                isAvailable: true,
                isDeliverable: true,
                rejectionReason: null,
                userId: 1,
                buyerId: null,
                dateBought: null,
                deletedAfterSold: false
            };
            testProductService.create(product1).catch(err => {
                expect(err).not.to.be.null;
            });
        });
    });
    describe('Test update product', () => {
        it('can update product', function() {
            const product1: ProductAttributes = {
                productId: 1,
                isApproved: false,
                title: 'Phone',
                isProduct: true,
                price: 300,
                description: 'IPhone X',
                location: 'Freiburg',
                isSelling: true,
                isAvailable: true,
                isDeliverable: false,
                rejectionReason: null,
                userId: 1,
                buyerId: null,
                dateBought: null,
                deletedAfterSold: false
            };
            testProductService.update(1, product1).then(updated => {
                expect(updated.location).to.be.eq('Freiburg');
                expect(updated.isDeliverable).to.be.false;
            });
        });
        it('can not update product with unallowed data', function() {
            const product1: ProductAttributes = {
                productId: 1,
                isApproved: false,
                title: null,
                isProduct: null,
                price: 300,
                description: 'IPhone X',
                location: 'Bern',
                isSelling: true,
                isAvailable: true,
                isDeliverable: true,
                rejectionReason: null,
                userId: 1,
                buyerId: null,
                dateBought: null,
                deletedAfterSold: false
            };
            testProductService.update(1, product1).catch(err => {
                expect(err).not.to.be.null;
            });
        });
        it('can not update product which does not exist', function() {
            const product10: ProductAttributes = {
                productId: 10,
                isApproved: false,
                title: 'Phone',
                isProduct: true,
                price: 300,
                description: 'IPhone X',
                location: 'Bern',
                isSelling: true,
                isAvailable: true,
                isDeliverable: true,
                rejectionReason: null,
                userId: 1,
                buyerId: null,
                dateBought: null,
                deletedAfterSold: false
            };
            testProductService.update(10, product10).catch(err => {
                expect(err).not.to.be.null;
            });
        });
    });
    describe('Test approve product', () => {
        it('can approve products', function() {
            testProductService.approve(1).then(approved => {
                expect(approved.isApproved).to.be.true;
            });
        });
        it('can not approve product which does not exist', function() {
            testProductService.approve(2).catch(err => {
                expect(err).not.to.be.null;
            });
        });
    });
    describe('Test reject product', () => {
        it('can reject products', function() {
            testProductService.reject(1, 'Please enter a better description!').then(rejected => {
                expect(rejected.isApproved).to.be.false;
                expect(rejected.rejectionReason).to.be.eq('Please enter a better description!');
            });
        });
        it('can not reject product which does not exist', function() {
            testProductService.reject(2, 'Please enter a better description!').catch(err => {
                expect(err).not.to.be.null;
            });
        });
    });
    describe('Test delete product', () => {
        it('can delete product', function() {
            testProductService.delete(1).then(() => {
                Product.findOne({
                    where: {
                        title: 'Phone'
                    }
                }).then(foundProduct => {
                    expect(foundProduct).to.be.eq(null);
                });
            });
        });
    });
});

import { SearchRequest } from '../models/search.model';
import { Product, ProductAttributes } from './../models/product.model';

export class ProductService {

    public create(product: ProductAttributes): Promise<Product> {
        return Product.create(product)
        .then(inserted => Promise.resolve(inserted))
        .catch(err => Promise.reject(err));
    }

    public update(productId: number, product: ProductAttributes): Promise<Product> {
        return Product.findByPk(productId)
        .then(found =>
                found.update(product)
                .then(() => { return Promise.resolve(found);
                })
        )
        .catch(err => Promise.reject(err));
    }

    public approve(productId: number): Promise<Product> {
        return Product.findByPk(productId)
        .then(found =>
            found.update({isApproved: true})
            .then(() => { return Promise.resolve(found);
            })
        )
        .catch(err => Promise.reject(err));
    }

    public reject(productId: number, rejectionReason: string): Promise<Product> {
        return Product.findByPk(productId)
        .then(found =>
            found.update({isApproved: false, rejectionReason: rejectionReason})
            .then(() => { return Promise.resolve(found);
            })
        )
        .catch(err => Promise.reject(err));
    }

    public delete(productId: number): Promise<Product> {
        return Product.findByPk(productId)
        .then(found =>
            found.destroy()
            .then(() => Promise.resolve(found))
        )
        .catch(err => Promise.reject(err));
    }

    public getAllProducts(): Promise<Product[]> {
        return Product.findAll();
    }

    public getApprovedProducts(): Promise<Product[]> {
        return Product.findAll({
            where: {
                isApproved: true
            }
        });
    }

    public getRejectedProducts(): Promise<Product[]> {
        const { Op } = require('sequelize');
        return Product.findAll({
            where: {
                [ Op.and ]: [
                    { isApproved: false },
                    { rejectionReason: { [ Op.not ]: null }}
                ]
            }
        });
    }

    public getUncheckedProducts(): Promise<Product[]> {
        const { Op } = require('sequelize');
        return Product.findAll({
            where: {
                [ Op.and ]: [
                    { isApproved: false },
                    { rejectionReason: null}
                ]
            }
        });
    }

    public getMyProducts(thisUserId: number): Promise<Product[]> {
        const { Op } = require('sequelize');
        return Product.findAll({
            where: {
                [ Op.and ]: [
                    { buyerId: thisUserId},
                    { deletedAfterSold: false }
                ]
            }
        });
    }

    public getProduct(thisProductId: number): Promise<Product> {
        return Product.findOne({
            where: {
                productId: thisProductId
            }
        });
    }

    public getMyRejectedProducts(thisUserId: number): Promise<Product[]> {
        const { Op } = require('sequelize');
        return Product.findAll({
            where: {
                [ Op.and ]: [
                    { userId: thisUserId },
                    { isApproved: false},
                    { rejectionReason: { [ Op.not ]: null }}
                ]
            }
        });
    }

    public getMyPendingProducts(thisUserId: number): Promise<Product[]> {
        const { Op } = require('sequelize');
        return Product.findAll({
            where: {
                [ Op.and ]: [
                    { userId: thisUserId},
                    { isApproved: false},
                    { rejectionReason: null}
                ]
            }
        });
    }

    public getMyApprovedProducts(thisUserId: number): Promise<Product[]> {
        const { Op } = require('sequelize');
        return Product.findAll({
            where: {
                [ Op.and ]: [
                    { userId: thisUserId},
                    { isApproved: true},
                    { isAvailable: true }
                ]
            }
        });
    }

    public getProductsIBought(thisUserId: number): Promise<Product[]> {
        const { Op } = require('sequelize');
        return Product.findAll({
            where: {
                [ Op.and ]: [
                    { buyerId: thisUserId},
                    { isProduct: true },
                    { isSelling: true }
                ]
            }
        });
    }

    public getProductsImRenting(thisUserId: number): Promise<Product[]> {
        const { Op } = require('sequelize');
        return Product.findAll({
            where: {
                [ Op.and ]: [
                    { buyerId: thisUserId},
                    { isProduct: true },
                    { isSelling: false }
                ]
            }
        });
    }

    public getServiceImUtilize(thisUserId: number): Promise<Product[]> {
        const { Op } = require('sequelize');
        return Product.findAll({
            where: {
                [ Op.and ]: [
                    { buyerId: thisUserId},
                    { isProduct: false },
                ]
            }
        });
    }

    public getMySoldProducts(thisUserId: number): Promise<Product[]> {
        const { Op } = require('sequelize');
        return Product.findAll({
            where: {
                [ Op.and ]: [
                    { userId: thisUserId },
                    { buyerId: { [ Op.not ]: null }},
                    { deletedAfterSold: false }
                ]
            }
        });
    }

    public deleteProductAfterSold(productId: number): Promise<Product> {
        return Product.findByPk(productId)
        .then(found =>
            found.update({deletedAfterSold: true})
            .then(() => { return Promise.resolve(found);
            })
        )
        .catch(err => Promise.reject(err));
    }

    public search(filters: SearchRequest): Promise<Product[]> {
        const { Op } = require('sequelize');

        const options: any = {};

        if (filters.title) {
            options.title = {
                [ Op.substring ]: filters.title
            };
        }

        if (filters.location) {
            options.location = {
                [ Op.substring ]: filters.location
            };
        }

        if (filters.minPrice && filters.maxPrice) {
            options.price = {
                [ Op.between ]: [ filters.minPrice, filters.maxPrice]
            };
        } else if (filters.minPrice && !filters.maxPrice) {
            options.price = {
                [ Op.gte ]: filters.minPrice
            };
        } else if (!filters.minPrice && filters.maxPrice) {
            options.price = {
                [ Op.lte ]: filters.maxPrice
            };
        }

        if (filters.isDeliverable != null) {
            options.isDeliverable = {
                [ Op.is ]: filters.isDeliverable
            };
        }

        if (filters.isProduct != null) {
            options.isProduct = {
                [ Op.is ]: filters.isProduct
            };
        }

        if (filters.isSelling != null) {
            options.isSelling = {
                [ Op.is ]: filters.isSelling
            };
        }

        options.isApproved = {
            [ Op.is ]: true
        };

        return Product.findAll({
            where: options
        });
    }
}

import { Optional, Model, Sequelize, DataTypes } from 'sequelize';
import { User } from './user.model';

export interface ProductAttributes {
    productId: number;
    isApproved: boolean;
    title: string;
    isProduct: boolean;
    price: number;
    description: string;
    location: string;
    isSelling: boolean;
    isAvailable: boolean;
    isDeliverable: boolean;
    rejectionReason: string;
    userId: number;
    buyerId: number;
    dateBought: Date;
    deletedAfterSold: boolean;
}

export interface ProductCreationAttributes extends Optional<ProductAttributes, 'productId'> { }

export class Product extends Model<ProductAttributes, ProductCreationAttributes> implements ProductAttributes {
    productId!: number;
    isApproved!: boolean;
    title!: string;
    isProduct!: boolean;
    price!: number;
    description!: string;
    location!: string;
    isSelling!: boolean;
    isAvailable!: boolean;
    isDeliverable!: boolean;
    rejectionReason!: string;
    userId!: number;
    buyerId!: number;
    dateBought!: Date;
    deletedAfterSold!: boolean;

    public static initialize(sequelize: Sequelize) {
        Product.init({
            productId: {
                type: DataTypes.INTEGER,
                autoIncrement: true,
                primaryKey: true
            },
            isApproved: {
                type: DataTypes.BOOLEAN,
                defaultValue: false,
                allowNull: false
            },
            title: {
                type: DataTypes.STRING,
                allowNull: false
            },
            isProduct: {
                type: DataTypes.BOOLEAN,
                allowNull: false
            },
            price: {
                type: DataTypes.NUMBER,
                allowNull: false
            },
            description: {
                type: DataTypes.STRING,
                allowNull: false
            },
            location: {
                type: DataTypes.STRING,
                allowNull: false
            },
            isSelling: {
                type: DataTypes.BOOLEAN,
                allowNull: false
            },
            isAvailable: {
                type: DataTypes.BOOLEAN,
                allowNull: false
            },
            isDeliverable: {
                type: DataTypes.BOOLEAN,
                allowNull: false
            },
            rejectionReason: {
                type: DataTypes.STRING
            },
            userId: {
                type: DataTypes.INTEGER,
                allowNull: false
            },
            buyerId: {
                type: DataTypes.INTEGER
            },
            dateBought: {
                type: DataTypes.DATE
            },
            deletedAfterSold: {
                type: DataTypes.BOOLEAN,
                defaultValue: false,
                allowNull: false
            }
        },
            {
                sequelize,
                tableName: 'products'
            }
        );
    }

    public static createAssociations() {
        Product.belongsTo(User, {
            targetKey: 'userId',
            as: 'user',
            onDelete: 'cascade',
            foreignKey: 'userId'
        });
    }
}

